/*
CHECK AVAILABILITY
*/
function check_availability(element_id, table_name, table_field) {
	
	var field_data = jQuery("#" + element_id).val();
	//if (field_data.length > 3) {
		jQuery("#availability_status").html('<img src="images/preloader_black.gif" align="absmiddle">&nbsp;Controllo disponibilit&aacute;...');
		jQuery.ajax({
	    type: "POST",  
	    url: "php/check_availability.php",
	    data: "value=" + field_data + "&table_field=" + table_field + "&table_name=" + table_name,
	    success: function(server_response) {
				jQuery("#availability_status").ajaxComplete(function(event, request) { 
					if (server_response == '0')	{ 
						jQuery("#availability_status").html('<img src="images/available.png" align="absmiddle">Disponibile!');
					} else if (server_response == '1') {
						jQuery("#availability_status").html('<img src="images/not_available.png" align="absmiddle">Non disponibile!');
					}
				});
			},
			error: function() {}
		});
	//}
	return false;
}
